import { useQuery } from "@tanstack/react-query";
import { Header } from "@/components/layout/header";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Skeleton } from "@/components/ui/skeleton";
import { RevenueChart } from "@/components/charts/revenue-chart";
import { AISearch } from "@/components/ui/ai-search";
import { ClientCheckin } from "@/components/ui/client-checkin";
import { useLanguage } from "@/contexts/language-context";
import { api } from "@/lib/api";
import { DashboardStats, RecentActivity, CourtDate } from "@/lib/types";

export default function Dashboard() {
  const { t } = useLanguage();

  const { data: stats, isLoading: statsLoading } = useQuery<DashboardStats>({
    queryKey: ["/api/dashboard/stats"],
    queryFn: api.getDashboardStats,
  });

  const { data: recentActivity = [], isLoading: activityLoading } = useQuery<RecentActivity[]>({
    queryKey: ["/api/dashboard/recent-activity"],
    queryFn: () => api.getRecentActivity(10),
  });

  const { data: courtDates = [], isLoading: courtDatesLoading } = useQuery<CourtDate[]>({
    queryKey: ["/api/dashboard/upcoming-court-dates"],
    queryFn: () => api.getUpcomingCourtDates(10),
  });

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
    }).format(amount);
  };

  const getActivityIcon = (action: string) => {
    switch (action.toLowerCase()) {
      case 'create':
      case 'created':
        return "fas fa-plus text-blue-600";
      case 'payment':
      case 'paid':
        return "fas fa-check text-green-600";
      case 'reminder':
      case 'alert':
        return "fas fa-exclamation text-amber-600";
      default:
        return "fas fa-info text-gray-600";
    }
  };

  const formatDate = (dateString: string) => {
    try {
      return new Date(dateString).toLocaleDateString('en-US', {
        month: 'short',
        day: 'numeric',
        year: 'numeric',
      });
    } catch {
      return dateString;
    }
  };

  const formatTime = (dateString: string) => {
    try {
      return new Date(dateString).toLocaleTimeString('en-US', {
        hour: 'numeric',
        minute: '2-digit',
        hour12: true,
      });
    } catch {
      return '';
    }
  };

  return (
    <div className="flex-1 overflow-hidden">
      <Header
        title={t('dashboard.title')}
        subtitle={t('dashboard.overview')}
      />

      <div className="p-6 overflow-y-auto h-full">
        {/* AI Search and Client Check-in */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
          <AISearch />
          <ClientCheckin />
        </div>

        {/* Dashboard Stats */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <Card data-testid="stat-active-bonds">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-muted-foreground text-sm font-medium">Active Bonds</p>
                  {statsLoading ? (
                    <Skeleton className="h-8 w-16 mt-1" />
                  ) : (
                    <p className="text-3xl font-bold text-foreground" data-testid="value-active-bonds">
                      {stats?.activeBonds || 0}
                    </p>
                  )}
                </div>
                <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center">
                  <i className="fas fa-handshake text-blue-600 text-xl"></i>
                </div>
              </div>
              <div className="mt-4 flex items-center">
                <span className="text-green-600 text-sm font-medium">+12%</span>
                <span className="text-muted-foreground text-sm ml-1">from last month</span>
              </div>
            </CardContent>
          </Card>

          <Card data-testid="stat-total-revenue">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-muted-foreground text-sm font-medium">Total Revenue</p>
                  {statsLoading ? (
                    <Skeleton className="h-8 w-24 mt-1" />
                  ) : (
                    <p className="text-3xl font-bold text-foreground" data-testid="value-total-revenue">
                      {formatCurrency(stats?.totalRevenue || 0)}
                    </p>
                  )}
                </div>
                <div className="w-12 h-12 bg-green-100 rounded-lg flex items-center justify-center">
                  <i className="fas fa-dollar-sign text-green-600 text-xl"></i>
                </div>
              </div>
              <div className="mt-4 flex items-center">
                <span className="text-green-600 text-sm font-medium">+8%</span>
                <span className="text-muted-foreground text-sm ml-1">from last month</span>
              </div>
            </CardContent>
          </Card>

          <Card data-testid="stat-pending-payments">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-muted-foreground text-sm font-medium">Pending Payments</p>
                  {statsLoading ? (
                    <Skeleton className="h-8 w-20 mt-1" />
                  ) : (
                    <p className="text-3xl font-bold text-foreground" data-testid="value-pending-payments">
                      {formatCurrency(stats?.pendingPayments || 0)}
                    </p>
                  )}
                </div>
                <div className="w-12 h-12 bg-amber-100 rounded-lg flex items-center justify-center">
                  <i className="fas fa-clock text-amber-600 text-xl"></i>
                </div>
              </div>
              <div className="mt-4 flex items-center">
                <span className="text-destructive text-sm font-medium">-3%</span>
                <span className="text-muted-foreground text-sm ml-1">from last month</span>
              </div>
            </CardContent>
          </Card>

          <Card data-testid="stat-court-dates">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-muted-foreground text-sm font-medium">Court Dates</p>
                  {statsLoading ? (
                    <Skeleton className="h-8 w-12 mt-1" />
                  ) : (
                    <p className="text-3xl font-bold text-foreground" data-testid="value-court-dates">
                      {stats?.upcomingCourtDates || 0}
                    </p>
                  )}
                </div>
                <div className="w-12 h-12 bg-purple-100 rounded-lg flex items-center justify-center">
                  <i className="fas fa-calendar text-purple-600 text-xl"></i>
                </div>
              </div>
              <div className="mt-4 flex items-center">
                <span className="text-muted-foreground text-sm">Next: {courtDates[0] ? formatDate(courtDates[0].courtDate) : 'None scheduled'}</span>
              </div>
            </CardContent>
          </Card>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
          {/* Recent Activity */}
          <Card data-testid="recent-activity-card">
            <CardHeader>
              <CardTitle>Recent Activity</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {activityLoading ? (
                  Array.from({ length: 3 }).map((_, i) => (
                    <div key={i} className="flex items-start space-x-3">
                      <Skeleton className="w-8 h-8 rounded-full" />
                      <div className="flex-1">
                        <Skeleton className="h-4 w-3/4 mb-1" />
                        <Skeleton className="h-3 w-1/2" />
                      </div>
                    </div>
                  ))
                ) : recentActivity.length > 0 ? (
                  recentActivity.map((activity) => (
                    <div key={activity.id} className="flex items-start space-x-3" data-testid={`activity-${activity.id}`}>
                      <div className="w-8 h-8 bg-blue-100 rounded-full flex items-center justify-center flex-shrink-0">
                        <i className={getActivityIcon(activity.action)} style={{ fontSize: '14px' }}></i>
                      </div>
                      <div className="flex-1 min-w-0">
                        <p className="text-sm text-foreground">
                          {activity.action} for{' '}
                          <span className="font-medium">
                            {activity.firstName} {activity.lastName}
                          </span>
                        </p>
                        <p className="text-xs text-muted-foreground">
                          {formatDate(activity.createdAt)}
                        </p>
                      </div>
                    </div>
                  ))
                ) : (
                  <p className="text-muted-foreground text-sm">No recent activity</p>
                )}
              </div>
            </CardContent>
          </Card>

          {/* Upcoming Court Dates */}
          <Card data-testid="court-dates-card">
            <CardHeader>
              <CardTitle>Upcoming Court Dates</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-3">
                {courtDatesLoading ? (
                  Array.from({ length: 3 }).map((_, i) => (
                    <Skeleton key={i} className="h-16 w-full rounded-lg" />
                  ))
                ) : courtDates.length > 0 ? (
                  courtDates.map((courtDate, index) => (
                    <div
                      key={courtDate.id}
                      className={`flex items-center justify-between p-4 bg-secondary/10 border border-border rounded-lg mb-3 ${
                        index !== courtDates.length - 1 ? 'border-b' : ''
                      }`}
                      data-testid={`court-date-${courtDate.id}`}
                      style={{ marginBottom: index !== courtDates.length - 1 ? '12px' : '0' }}
                    >
                      <div className="flex-1">
                        <p className="text-sm font-semibold text-foreground mb-1">
                          {courtDate.firstName} {courtDate.lastName}
                        </p>
                        <p className="text-xs text-muted-foreground">
                          Case #{courtDate.caseNumber}
                        </p>
                      </div>
                      <div className="text-right flex-shrink-0 ml-4">
                        <p className="text-sm font-semibold text-foreground mb-1">
                          {formatDate(courtDate.courtDate)}
                        </p>
                        <p className="text-xs text-muted-foreground">
                          {formatTime(courtDate.courtDate)}
                        </p>
                      </div>
                    </div>
                  ))
                ) : (
                  <p className="text-muted-foreground text-sm">No upcoming court dates</p>
                )}
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Revenue Chart */}
        <Card data-testid="revenue-chart-card">
          <CardHeader>
            <CardTitle>Revenue Trends</CardTitle>
            <CardDescription>Monthly revenue performance over time</CardDescription>
          </CardHeader>
          <CardContent>
            <RevenueChart />
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/documents.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Header } from "@/components/layout/header";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Skeleton } from "@/components/ui/skeleton";
import { UploadDocumentModal } from "@/components/modals/upload-document-modal";
import { api } from "@/lib/api";

export default function Documents() {
  const [categoryFilter, setCategoryFilter] = useState<string>("");
  const [uploadModalOpen, setUploadModalOpen] = useState(false);

  const { data: documents = [], isLoading } = useQuery({
    queryKey: ["/api/documents", { category: categoryFilter }],
    queryFn: () => api.getDocuments({ category: categoryFilter }),
  });

  // Calculate document statistics by category
  const documentStats = {
    contracts: documents.filter((d: any) => d.category === 'contract').length,
    courtPapers: documents.filter((d: any) => d.category === 'court_papers').length,
    identification: documents.filter((d: any) => d.category === 'identification').length,
    financial: documents.filter((d: any) => d.category === 'financial').length,
  };

  const filteredDocuments = categoryFilter && categoryFilter !== "all"
    ? documents.filter((doc: any) => doc.category === categoryFilter)
    : documents;

  const getCategoryIcon = (category: string) => {
    switch (category) {
      case "contract":
        return "fas fa-file-contract text-blue-600";
      case "court_papers":
        return "fas fa-gavel text-purple-600";
      case "identification":
        return "fas fa-id-card text-green-600";
      case "financial":
        return "fas fa-receipt text-amber-600";
      default:
        return "fas fa-file text-gray-600";
    }
  };

  const getFileIcon = (mimeType: string) => {
    if (mimeType?.includes('pdf')) return "fas fa-file-pdf text-red-600";
    if (mimeType?.includes('image')) return "fas fa-file-image text-green-600";
    if (mimeType?.includes('word')) return "fas fa-file-word text-blue-600";
    return "fas fa-file text-gray-600";
  };

  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    });
  };

  const getCategoryLabel = (category: string) => {
    switch (category) {
      case "contract":
        return "Contract";
      case "court_papers":
        return "Court Papers";
      case "identification":
        return "Identification";
      case "financial":
        return "Financial";
      default:
        return category;
    }
  };

  return (
    <div className="flex-1 overflow-hidden">
      <Header
        title="Document Management"
        subtitle="Manage contracts, court papers, and client documents"
        showNewBondButton={false}
      />

      <div className="p-6 overflow-y-auto h-full">
        <div className="flex items-center justify-between mb-6">
          <div>
            <h3 className="text-xl font-semibold text-foreground">Document Management</h3>
            <p className="text-muted-foreground">Manage contracts, court papers, and client documents</p>
          </div>
          <Button 
            data-testid="button-upload-document"
            onClick={() => setUploadModalOpen(true)}
          >
            <i className="fas fa-upload mr-2"></i>Upload Document
          </Button>
        </div>

        {/* Document Categories */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
          <Card data-testid="stat-contracts" className="cursor-pointer hover:bg-accent transition-colors" onClick={() => setCategoryFilter(categoryFilter === 'contract' ? '' : 'contract')}>
            <CardContent className="p-6 text-center">
              <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center mx-auto mb-4">
                <i className="fas fa-file-contract text-blue-600 text-xl"></i>
              </div>
              <h4 className="text-lg font-semibold text-foreground mb-2">Contracts</h4>
              <p className="text-2xl font-bold text-foreground" data-testid="value-contracts">
                {documentStats.contracts}
              </p>
            </CardContent>
          </Card>

          <Card data-testid="stat-court-papers" className="cursor-pointer hover:bg-accent transition-colors" onClick={() => setCategoryFilter(categoryFilter === 'court_papers' ? '' : 'court_papers')}>
            <CardContent className="p-6 text-center">
              <div className="w-12 h-12 bg-purple-100 rounded-lg flex items-center justify-center mx-auto mb-4">
                <i className="fas fa-gavel text-purple-600 text-xl"></i>
              </div>
              <h4 className="text-lg font-semibold text-foreground mb-2">Court Papers</h4>
              <p className="text-2xl font-bold text-foreground" data-testid="value-court-papers">
                {documentStats.courtPapers}
              </p>
            </CardContent>
          </Card>

          <Card data-testid="stat-identification" className="cursor-pointer hover:bg-accent transition-colors" onClick={() => setCategoryFilter(categoryFilter === 'identification' ? '' : 'identification')}>
            <CardContent className="p-6 text-center">
              <div className="w-12 h-12 bg-green-100 rounded-lg flex items-center justify-center mx-auto mb-4">
                <i className="fas fa-id-card text-green-600 text-xl"></i>
              </div>
              <h4 className="text-lg font-semibold text-foreground mb-2">Identification</h4>
              <p className="text-2xl font-bold text-foreground" data-testid="value-identification">
                {documentStats.identification}
              </p>
            </CardContent>
          </Card>

          <Card data-testid="stat-financial-docs" className="cursor-pointer hover:bg-accent transition-colors" onClick={() => setCategoryFilter(categoryFilter === 'financial' ? '' : 'financial')}>
            <CardContent className="p-6 text-center">
              <div className="w-12 h-12 bg-amber-100 rounded-lg flex items-center justify-center mx-auto mb-4">
                <i className="fas fa-receipt text-amber-600 text-xl"></i>
              </div>
              <h4 className="text-lg font-semibold text-foreground mb-2">Financial</h4>
              <p className="text-2xl font-bold text-foreground" data-testid="value-financial-docs">
                {documentStats.financial}
              </p>
            </CardContent>
          </Card>
        </div>

        {/* Document List */}
        <Card>
          <CardHeader>
            <div className="flex items-center justify-between">
              <CardTitle>
                {categoryFilter ? `${getCategoryLabel(categoryFilter)} Documents` : 'Recent Documents'}
              </CardTitle>
              <div className="flex space-x-2">
                <Select value={categoryFilter} onValueChange={setCategoryFilter}>
                  <SelectTrigger className="w-48" data-testid="select-category-filter">
                    <SelectValue placeholder="All Categories" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Categories</SelectItem>
                    <SelectItem value="contract">Contracts</SelectItem>
                    <SelectItem value="court_papers">Court Papers</SelectItem>
                    <SelectItem value="identification">Identification</SelectItem>
                    <SelectItem value="financial">Financial</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>
          </CardHeader>
          <CardContent className="p-6">
            <div className="space-y-4">
              {isLoading ? (
                Array.from({ length: 5 }).map((_, i) => (
                  <div key={i} className="flex items-center justify-between p-4 border border-border rounded-lg">
                    <div className="flex items-center space-x-4">
                      <Skeleton className="w-10 h-10 rounded-lg" />
                      <div>
                        <Skeleton className="h-4 w-48 mb-1" />
                        <Skeleton className="h-3 w-32" />
                      </div>
                    </div>
                    <div className="flex space-x-2">
                      <Skeleton className="h-4 w-4" />
                      <Skeleton className="h-4 w-4" />
                      <Skeleton className="h-4 w-4" />
                    </div>
                  </div>
                ))
              ) : filteredDocuments.length > 0 ? (
                filteredDocuments.map((document: any) => (
                  <div
                    key={document.id}
                    className="flex items-center justify-between p-4 border border-border rounded-lg hover:bg-muted/50 transition-colors"
                    data-testid={`document-row-${document.id}`}
                  >
                    <div className="flex items-center space-x-4">
                      <div className="w-10 h-10 bg-secondary rounded-lg flex items-center justify-center">
                        <i className={getFileIcon(document.mimeType)}></i>
                      </div>
                      <div>
                        <h5 className="text-sm font-medium text-foreground" data-testid={`document-name-${document.id}`}>
                          {document.originalName}
                        </h5>
                        <p className="text-xs text-muted-foreground">
                          {getCategoryLabel(document.category)} â€¢ Uploaded {formatDate(document.createdAt)} â€¢ {formatFileSize(document.fileSize)}
                        </p>
                      </div>
                    </div>
                    <div className="flex space-x-2">
                      <button 
                        className="text-primary hover:text-primary/80" 
                        title="View"
                        data-testid={`button-view-${document.id}`}
                      >
                        <i className="fas fa-eye"></i>
                      </button>
                      <button 
                        className="text-muted-foreground hover:text-foreground" 
                        title="Download"
                        data-testid={`button-download-${document.id}`}
                      >
                        <i className="fas fa-download"></i>
                      </button>
                      <button 
                        className="text-muted-foreground hover:text-foreground" 
                        title="Share"
                        data-testid={`button-share-${document.id}`}
                      >
                        <i className="fas fa-share"></i>
                      </button>
                    </div>
                  </div>
                ))
              ) : (
                <div className="text-center py-12">
                  <div className="text-muted-foreground">
                    {categoryFilter ? (
                      <>
                        <i className={`${getCategoryIcon(categoryFilter)} text-4xl mb-4 block opacity-50`}></i>
                        <p className="text-lg mb-2">No {getCategoryLabel(categoryFilter).toLowerCase()} documents found</p>
                        <p className="text-sm">Upload your first {getCategoryLabel(categoryFilter).toLowerCase()} document to get started</p>
                      </>
                    ) : (
                      <>
                        <i className="fas fa-file-alt text-4xl mb-4 block opacity-50"></i>
                        <p className="text-lg mb-2">No documents yet</p>
                        <p className="text-sm">Upload your first document to get started</p>
                      </>
                    )}
                  </div>
                </div>
              )}
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Upload Document Modal */}
      <UploadDocumentModal
        open={uploadModalOpen}
        onOpenChange={setUploadModalOpen}
      />
    </div>
  );
}

