                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Defense Attorney</FormLabel>
                    <FormControl>
                      <Input {...field} data-testid="input-defense-attorney" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="notes"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Notes</FormLabel>
                  <FormControl>
                    <Textarea {...field} rows={3} data-testid="input-notes" />
                  </FormControl>
                  <FormMessage />
