  const [currentSteps, setCurrentSteps] = useState(steps);
  const progress = (completedSteps.size / steps.length) * 100;

  const markStepCompleted = (stepId: string) => {
    setCompletedSteps(prev => new Set([...Array.from(prev), stepId]));
